INSERT INTO `FUK_AKTIVNOST` VALUES (4113,3088,3112,'Достава Плана набавке Служби комерцијалних послова на реализацију.',NULL,4,'','Материјал за допуну допуну ормарића и набавку нових ормарића за прву помоћ, обавља се преко Службе комерцијалних послова.','Сарадник за послове БЗР,Служба комерцијалних послова, сарадник за извршне набавке.','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:12');
INSERT INTO `FUK_AKTIVNOST` VALUES (4114,3088,3112,'Преузимање набављеног материјала из Централног магацина',NULL,5,'Налог магацину','Након набавке потребног материјала, Служба комерцијалних послова робу доставља у Централни магацин, о чему обавештава Службу БЗР.','Служба комерцијалних послова','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:12');
INSERT INTO `FUK_AKTIVNOST` VALUES (4115,3088,3112,'Опремање и допуна ормарића прве медицинске помоћи, по локацијама у предузећу.',NULL,6,'','Након преузимања из Централног магацина, служба БЗР доставља санитетски материјал, за допуну кутија прве медицинске помоћи, по локацијама.','Сарадник за послове БЗР','По потрошњи',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:12');
INSERT INTO `FUK_AKTIVNOST` VALUES (4116,3088,3112,'Контрола',NULL,7,'','Руководилац контролише, обим набављеног и достављеног сантитетског материјала и ормарића за прву помоћ.','Руководилац Службе БЗР','',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:13');
