INSERT INTO `FUK_AKTIVNOST` VALUES (4120,3088,3113,'Пријава повреде на раду запосленог.',NULL,2,'Пријава повреде на раду, Изјава о повреди на раду повређеног и очевица.','Након пружања прве помоћи и збрињавања повређеног у здравствену установу, непосредни руководилац повређеног, Служби БЗР пријављује повреду на раду.','Непосредни руководилац повређеног','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:13');
INSERT INTO `FUK_AKTIVNOST` VALUES (4121,3088,3113,'Пријава повреде на раду запосленог, од стране Службе БЗР.',NULL,3,'Образац 1 (Извештај о повреди на раду).','Повреда на раду се пријављује у року од 24 часа, Инспекцији рада и МУП-у, у складу са Законом о БЗР.','Сарадник за послове БЗР','24 часа',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:13');
INSERT INTO `FUK_AKTIVNOST` VALUES (4122,3088,3113,'Контрола',NULL,4,'Образац 1(Извештај о повреди на раду).','Контрола законског рока за пријавуповреде на раду и извештаја о повреди на раду.','Руководилац Сужбе БЗР','24 сата',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:13');
INSERT INTO `FUK_AKTIVNOST` VALUES (4123,3088,3113,'Упућивање запосленог са попуњеним Извештајем о повреди на раду у здравствену установу',NULL,5,'Извештај о повреди на раду.','Запослени повређени добија попуњен Извештај о повреди на раду (у пет примерка), из Службе БЗР,и доставља газдравственој установи, у којој је  је извршен први преглед повређеног.','Сарадник за послове БЗР','24 сата',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:13');
INSERT INTO `FUK_AKTIVNOST` VALUES (4124,3088,3113,'Повраћај попуњеног извештаја од стране лекара који је указао помоћ повређеном.',NULL,6,'Извештај о повреди на раду.','Попуњени извештај од стране лекара са налазом и мишљењем се у року од два дана, од дана пријема, доставља Служби БЗР.','Сарадник за послове БЗР','48 сати',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:13');
INSERT INTO `FUK_AKTIVNOST` VALUES (4125,3088,3113,'Достава Извештаја о повреди на раду, Републичком фонду за здравствено осигурање.',NULL,7,'Извештај о повреди на раду.','Попуњени Извештај се доставља на оверу филијали РФЗО код које повређени остварује права утврђена прописима о здравственом осигурању.','Сарадник за послове БЗР','По примљеном Извештају',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:13');
INSERT INTO `FUK_AKTIVNOST` VALUES (4126,3088,3113,'Овера извештаја и достава Служби БЗР.',NULL,8,'Извештај о повреди на раду.','Филијала РФЗО оверава свих пет примерака извештаја, један задржава за своје потребе а остале враћа послодавцу.','','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:13');
INSERT INTO `FUK_AKTIVNOST` VALUES (4127,3088,3113,'Достава Извештаја повређеном и надлежним институцијама',NULL,9,'Оверен Извештај.','Послодавац један примерак овереног извештаја задржава за своје потребе, један примерак извештаја доставља запосленом одмах, (најкасније у року од два дана од његовог пријема), један доставља филијали ПИО фонда и један примерак извештаја доставља Министарству за рад – Управи за безбедност и здравље на раду.','Сарадник за послове БЗР','Два дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:13');
INSERT INTO `FUK_AKTIVNOST` VALUES (4128,3088,3113,'Евиденција о повредама на раду запослених',NULL,10,'Образац 3(Образац евиденције о повредама на раду) и образац 11.','Служба БЗР води евиденцију о повредама на раду, за запослене.','Сарадник за послове БЗР','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:13');
