INSERT INTO `FUK_AKTIVNOST` VALUES (4132,3088,3114,'Успостављање контакта са представником ЗЗЗЗР.',NULL,3,'Уговор','Служба БЗР успоставља контакт са представником ЗЗЗЗР где ће запослени бити упућени на преглед.','Сарадник за пословеБЗР','Уговор,            По Плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:14');
INSERT INTO `FUK_AKTIVNOST` VALUES (4133,3088,3114,'Израда и достављање спискова запослених здравственој установи.',NULL,4,'Списак','Израда и достављање спискова запослених здравственој установи, свих заинтересованих за обављање прегледа.','Администратор за послове БЗР','По плану(пре прегледа)',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:14');
