INSERT INTO `FUK_AKTIVNOST` VALUES (4135,3088,3114,'Обављање прегледа.',NULL,6,'Упут','Запослени одлази у Здравствену установу на преглед.','Запослени','По Плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:14');
