INSERT INTO `FUK_AKTIVNOST` VALUES (4140,3088,3115,'Израда упутстава за безбедан и здрав рад (за запослене и опрему за рад).',NULL,2,'Акт о процени ризика,Закон о БЗР,Правилник о превентивним мерама при коришћењу опреме за рад,Правилник о прев.мерама за БЗР на радном месту,Извештај или записник инспектора рада, Важеће упутство или техничка документација.','Сарадник за послове БЗР сагледава потребе израде или ажурирања упутстава за рад (на радном месту и у радној околини), за потребе одређених процеса рада и радних места и израђује нова или ажурира стара упутства.','Сарадник за послове БЗР','По потреби',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:14');
INSERT INTO `FUK_AKTIVNOST` VALUES (4141,3088,3115,'Штампање и пластифицирање упустава.Одређивање локација за постављање упустава и постављање истих.',NULL,3,'','Сарадник и администратор за послове БЗР штампају и пласифицирају упутства, одређују локације за постављање и постављају упутства на видном месту.','Сарадник за послове БЗР','Након израде',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:14');
INSERT INTO `FUK_AKTIVNOST` VALUES (4142,3088,3115,'Контрола',NULL,4,'Упутства','Руководилац службе контролише израду,  достављање  и постављање упутстава.','Руководилац Службе БЗР','По поставља-њу упутстава.',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:14');
INSERT INTO `FUK_AKTIVNOST` VALUES (4143,3088,3115,'Провера функционалности постављених упустава.',NULL,5,'Упутства','Сарадник за послове БЗР контролише функционалност постављених упустава.','Сарадник за послове БЗР','По поставља-њу упутстава.',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:14');
