INSERT INTO `FUK_AKTIVNOST` VALUES (4146,3088,3116,'Израда Предлога за измену и допуну Акта о процени ризика.',NULL,2,'Колективни уговор о БЗР; Акт о процени ризика; Правилник о организаци-ји и системати-зацији послова у ЈКП „Медиана“ Ниш.','Служба БЗР израђујеПредлог измене и допуне Акта о процени ризика са сва нова радна места у предузећу или након евидентираних честих повреда на раду, који доставља директору, на основу којег директор доноси Одлуку о измени и допуни Акта.','Руководилац Службе БЗР','По потреби',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:15');
INSERT INTO `FUK_AKTIVNOST` VALUES (4147,3088,3116,'Достављање Одлуке о измени и допуни Акта, коју доноси директор.',NULL,3,'Одлука и Уговор','Након достављања Одлуке о измени и допуни Акта, коју доноси директор и њом одређује лице за БЗР које израђује План за спровођење допуне и измене Акта.','Руководилац Службе БЗР, сарадник за послове БЗР','Након Одлуке',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:15');
INSERT INTO `FUK_AKTIVNOST` VALUES (4148,3088,3116,'Испитивање тржишта за избор лица за спровођење Одлуке за измену и допуну Акта',NULL,4,'','На основу Одлуке бира се правно лице или предузетник са лиценцом за спровођење допуне Акта, са којим се потписује Уговор и одређује рок за израду и реализацију.','Сарадник за послове БЗР','Према Уговору',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:15');
INSERT INTO `FUK_AKTIVNOST` VALUES (4149,3088,3116,'Сарадник за послове БЗР и одабрано правно лице са лиценцом сарађују око спровођења допуне.Акта.',NULL,5,'','Сагледавају се сви ризици, све опасности и штетности на радном месту и у радној околини, на основу којих се предвиђају превентивне мере.','Руководилац Службе БЗР, сарадник за послове БЗР','Према Плану и Уговору',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:15');
INSERT INTO `FUK_AKTIVNOST` VALUES (4150,3088,3116,'Извршилац услуге доставља допуну Акта о процени ризика.',NULL,6,'Допуна Акта о процени ризика.','Служба БЗР добија допуну Акта о процени ризика за одређена радна места након чега приступа имплементацији Акта.','Сарадник за послове БЗР','Према Уговору',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:15');
