INSERT INTO `FUK_AKTIVNOST` VALUES (4234,3096,3126,'Овера техничке исправности',NULL,12,'','У путном налогу за путничка возила (QF-C-013) и/или путном налогу ТМВ (QF-А-019) контролор техничке исправности оверава техничку исправност возила (радне машине). Издавање путних налога за исправна возила контролор техничке исправности врши у току рада и на крају смене у 21:30h,  по  плану издавања путних налога за возила, водећи притом одговарајућу евиденцију у Извештају о раду  контролора (QF-C-015). Издате налоге предаје корисницима возила (радне машине).','Контролор техничке исправности','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:23');
INSERT INTO `FUK_AKTIVNOST` VALUES (4235,3096,3126,'Предаја возила (радне машине)',NULL,13,'','По завршетку контролног прегледа и добијања налога од контролора техничке исправности, паркирач возило (радну машину) премешта на паркинг исправних возила по плану паркирања и евидентира време паркирања у Радном налогу – листи превентивног одржавања (QF-C-002).','Паркирач, Контролор техничке исправности','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:23');
