INSERT INTO `FUK_AKTIVNOST` VALUES (4399,3106,3141,'Израдаоперативногплана',NULL,2,'План хватања паса по грaдским општинама','Директор сектора јавна хигијена са руководиоцем радне јединице З.О.О. – хигијена на основу усвојеног програма рада РЈ З.О.О.- хигијена за текућу годину, од стране Скупштине града сачињава план хватања паса по градским општинама.','Директор сектора одржавања хигијене,Руководилац РЈ,','До 25-ог у текућем месецу за наредни месец',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (4400,3106,3141,'Ванредни захтевиза извршењемактивности',NULL,3,'Евиденција позива,Евиденцијаизгубљених и напуштених паса,Пријава грађана','На основу решења комуналних инспекција градских општина, и телефонских позива комуналне полиције и трећих лица који се евидентирају у служби контакт центрa приступа се реализацији по ванредном захтеву.',',Руководилац РЈ,Служба контакт центар','По захтеву',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (4401,3106,3141,'Припрема зареализацијупланова',NULL,4,'Путни налог за возило,Радни налог  за возило,Радни налог за хватање паса','У фази припреме руководилац РЈ„З.О.О. – хигијена“  попуњава радни налог за возило a путни налог за возило возач добија из техничког сектора (служба логистике и контроле).         Радни налог за хватање паса            попуњава возач одмах по хватању паса а оверава  руководилац РЈ„З.О.О. – хигијена, к.','Руководилац РЈ','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (4402,3106,3141,'Раелизацијаплана рада',NULL,5,'Путни налог,Радни налог за хватање паса,Записник о враћању паса и мачака луталица, Записник о удомљавању - преузимању паса и мачака,Записник о враћању паса и мачака, Записник о стерилизацији напуштених паса и мачака луталица','Радна екипа на терену по радном налогу  приступа хватању паса и мачака луталица, исте смешта у кавез на камиону који се затим закључава и попуњава радни налог за хватање паса . По завршеном послу враћају се у прихватилиште. Уколико у поступку реализације својих активности радници наиђу на леш паса или мачака на јавним површинама исти уклањају и о таме сачињавају записник.','Руководилац РЈ,Радна екипа','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (4403,3106,3141,'Рализацијаплана рада',NULL,6,'Путни налог,Радни налог за хватање паса,Записник о враћању паса и мачака луталица, Записник о удомљавању - преузимању паса и мачака,Записник о враћању паса и мачака, Записник о стерилизацији напуштених паса и мачака луталица, Евиденција храњења, чишћења и појења паса, Записник о сакупљеним лешевима паса са јавних површина, Записник о одлагању у сточну јаму','Приликом истовара пса проверава се да ли поседује чип, ако поседује чип премешта се у кавез за несерилисане псе општине на чијој територији је ухваћен а ако не поседује приступа се чиповању, вакцинацији и смештању у кавез за нестерилисане псе општине на чијој територији је ухваћен. Приликом чиповања ветеринар отвара картон (пасош) са свим подацима о ухваћеном псу, који се чува у РЈ„З.О.О. – хигијена“ до тренутка удомљавања. Приликом удомљавања попуњава се записник о удомљавању – преузимању паса и мачака , а  пасош се даје новом власнику. Уколико не дође до удомљавања пас се после стерилизације и опоравка враћа на територију општине на којој је ухваћен о чему се сачињава записник о враћању паса и мачака луталица  а пасош остаје у РЈ З.О.О.-хигијена. Наведени записник потписује руководилац РЈ„З.О.О. – хигијена“, ветеринар и надзорни орган Дирекције за изградњу града. Уколико ухваћени пас поседује чип, што се идентификује читачем, подаци се одмах уписују у радни налог за хватање паса. . Ветеринар, за пса са чипом, проверава власништво и обавештава власника да је пас у прихватилишту и да може да се преузме Уколико  ухваћени пас не поседује чип, ветеринарска служба дан после хватања врши стерилизацију пса и попуњава записник о','Руководилац РЈ,Радна екипа','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (4404,3106,3141,'Реализацијаплана рада',NULL,7,'','стерилизацији паса и мачак, који потписују руководилац. Бригу о псима преузима радник РЈ„З.О.О. – хигијена“, који се стара о храњењу и одржавању хигијене у прихватилишту. Радник том приликом попуњава образац евиденција храњења, чишћења и појења паса .Уколико у прихватилишту угине пас или  животиња буде донешена од стране грађана или ветеринарске службе тело се одлаже у сточну јаму и о томе радник ЗОО хигијене попуњава записник на обрасцу .','','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:42');
