INSERT INTO `FUK_AKTIVNOST` VALUES (4406,3106,3141,'Надзор над            реализованим               радовима',NULL,9,'Радни налог за хватање паса,Грађевински дневник,Месечна ситуација ЗОО-хигијена,Пропратни лист пса','На основу попуњених радних налога за хватање паса сваког дана се попуњава грађевински дневник , који потписују руководилац РЈ З.О.О. – хигијена и надзорни орган Дирекције за изградњу града.На основу грађевинских дневника на крају месеца администратор сектора сачињава месечну финансијску ситуацију РЈ „З.О.О.-хигијена“.Сваки пас, за време боравка у прихватилишту, поседује Пропратни лист пса, образац који попуњава ветеринар.','Руководилац РЈ,Надзорни орган,Администратор','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (4407,3106,3141,'Евидентирање                активности',NULL,10,'Књига евиденције за РЈ ЗОО – хигијена, Бројно стање паса на дан','Све активности РЈ бележи руководилац РЈ„З.О.О. – хигијена“ сваког дана и књигу евиденције .Бројчано стање паса на  дан , документује се на обрасцу  а документ попуњава радник у прихватилишту.','Руководилац РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (4408,3106,3141,'Извештавање',NULL,11,'Књига евиденције за РЈ ЗОО – хигијена, Бројно стање паса на дан ,','Све активности РЈ бележи руководилац РЈ„З.О.О. – хигијена“ сваког дана и књигу евиденције .Бројчано стање паса на  дан , документује се на обрасцу  а документ попуњава радник у прихватилишту','Руководилац РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:42');
INSERT INTO `FUK_AKTIVNOST` VALUES (4409,3106,3141,'КРАЈ',NULL,12,'Месечни извештај о раду РЈ ЗОО- хигијена, Годишњи извештај о раду РЈ ЗОО- хигијена','Руководилац РЈ„З.О.О. – хигијена“ сачињава месечни извештај о раду РЈ и годишњи извештај о раду РЈ који   доставља директору сектора одржавања хигијене.  За свако решење комуналне инспекције градских општина руководилац РЈ даје одговор на меморандуму ЈКП „Медиана“, који потписује Директор. Верзију одговора руководилац РЈ„З.О.О. – хигијена“ прилаже уз одговарајуће решење и  архивира у регистар архиве РЈ.','Директор,Директор сектораРуководилац РЈ','',NULL,1,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:42');
