INSERT INTO `FUK_AKTIVNOST` VALUES (4448,3107,3146,'Исплата зарадИсплата зарада',NULL,8,'Обрачун зараде','Запослени прима накнаду по основу боловања на основу утврђеног обрачуна накнаде у статусу боловања – на основу збирног обрасца НЗ-10','Референт за обрачун зарада и накнада','Месечно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:40:45');
