INSERT INTO `FUK_AKTIVNOST` VALUES (4623,3121,3165,'Сагласност корисника',NULL,5,'Записник','Уз сагласност корисника услуга, референт за евиденцију стамбеног простора и референт за евиденцију пословног простора потписују Записник о утврђивању основа за обрачун накнаде за изношење отпада  и исти предају кориснику услуга на потписивање.','Референти стамбено – пословног простора ,;-Корисник услуга','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:02');
INSERT INTO `FUK_AKTIVNOST` VALUES (4624,3121,3165,'Дистрибуција Записника',NULL,6,'Записник','Дистрибуцију урађених записника  након обраде, врши администратор и то по документима: Списак записника датих за даљу обраду служби пореза и цена , Списак записника пословног простора датих на даљу обраду ОНА, Списак записника стамбеног простора датих на даљу обраду.','Администратор за евиденцију корисника услуга','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:03');
