INSERT INTO `FUK_AKTIVNOST` VALUES (4627,3122,3166,'Покретање ревизије',NULL,2,'годишњи план ревизије','Директор Центра за интерну ревизију покреће ревизију  на основу Годишњег плана интерне ревизије','Директор Центра за интерну ревизију','Према Годишњем плану ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:03');
