INSERT INTO `FUK_AKTIVNOST` VALUES (4629,3122,3166,'Обавештавање субјекта ревизије',NULL,4,'Писмено обавештeње субјекта ревизије','Писмено обавештавање субјектa ревизије о обављању планиране ревизије у којем се наводи  неопходна документација коју треба доставити Центру за интерну ревизију','Директор Центра за интерну ревизију','Одмах након добијања писменог налога за обављање ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:04');
INSERT INTO `FUK_AKTIVNOST` VALUES (4630,3122,3166,'Прелиминарни преглед',NULL,5,'','Прикупљање и анализа полазних информција о ревидираном подручју','Директор Центра за интерну ревизију','У периоду између обавештења и уводног састанка',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:04');
INSERT INTO `FUK_AKTIVNOST` VALUES (4631,3122,3166,'Утврђивање  плана ревизије',NULL,6,'Радни документ: План ревизије','Планом се  између осталог утврђују циљеви,обим,приступ и временски оквир ревизије.','Ревизорски тим','Пре уводног састанка',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:04');
INSERT INTO `FUK_AKTIVNOST` VALUES (4632,3122,3166,'Уводни састанак',NULL,7,'Записник са уводног састанка','Формални почетак ревизије означава састанак за покретање ревизије,који треба да се одржи између руководиоца субјекта ревизије и директора Центра за интерну ревизију,који ће бити у пратњи ревизора.','Ревизорски тим','Према року из овлашћења',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:04');
INSERT INTO `FUK_AKTIVNOST` VALUES (4633,3122,3166,'Ажурирање плана ревизије',NULL,8,'','Потврђивање или ажурирање  плана након обављеног уводног састанка.','Ревизорски тим','Непосредно након уводног састанка',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:04');
INSERT INTO `FUK_AKTIVNOST` VALUES (4634,3122,3166,'Утврђивање  циљева контроле и очекиваних контрола',NULL,9,'Радни документ:Упитник интерне контроле','Циљеви контрола треба да се односе на посебне карактеристике система и начин на који се активности унутар система спроводе са циљем да се испуне циљеви система.Очекиване контроле се дефинишу,да би  ревизорима пружиле смернице у погледу природе контрола које треба да траже у процесу идентификовања постојећих контрола у систему.','Интерни ревизор','Према року из плана ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:04');
INSERT INTO `FUK_AKTIVNOST` VALUES (4635,3122,3166,'Снимање система',NULL,10,'Радни документ:Опис система.','Обухвата интервјуисање запослених на основу припремњеног упитника, преглед документације,посматрање и остале технике  како би се утврдило како систем функционише .','Интерни ревизор','Према року из плана ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:04');
INSERT INTO `FUK_AKTIVNOST` VALUES (4636,3122,3166,'Потврда описа',NULL,11,'','Парафом на документу се потврђује да систем функционише на приказан начин.','Руководство система који је предмет ревизије','Одмах по формирању радног документа :Опис система',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:04');
INSERT INTO `FUK_AKTIVNOST` VALUES (4637,3122,3166,'Ход кроз систем тестови',NULL,12,'Радни документ:Ход кроз систем','Циљ ове врсте тестирања је да се обезбеди да систем функционише на начин који је описан у наративном делу или графикону система,као и да се потврди да у свакој фази постоје контроле.','Интерни ревизор','Према року из плана ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:04');
INSERT INTO `FUK_AKTIVNOST` VALUES (4638,3122,3166,'Контрола усаглашености',NULL,13,'','Потврда усаглашености приказа система са резултатима провере „ход кроз систем“.','Интерни ревизор','Упоредо са извођењем тестова „ХОД КРОЗ СИСТЕМ',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:04');
INSERT INTO `FUK_AKTIVNOST` VALUES (4639,3122,3166,'Почетна процена постојећих  унутрашњих   контрола',NULL,14,'Радни документ:Преглед контрола','Упоређивањем очекиваних и постојећих  унутрашњих контрола,утврђује се адекватност постојећих унутрашњих контрола','Интерни ревизор','Према року из плана ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:05');
INSERT INTO `FUK_AKTIVNOST` VALUES (4640,3122,3166,'Израда програма тестирања',NULL,15,'Радни документ:Тестирање контрола','Да би се утврдила доследна примена и делотворност постојећих контрола,израђује се Програм тестирања ,којим се одлучује која ће се тестирања спровести,којом методом и у ком времену.','Интерни ревизор','Према року из плана ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:05');
INSERT INTO `FUK_AKTIVNOST` VALUES (4641,3122,3166,'Одобрење тестирања',NULL,16,'Радни документи:Преглед контролаТестирање контрола','Одобрење програма тестирања','Директор Центра за интерну ревизију','Према року из плана ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:05');
INSERT INTO `FUK_AKTIVNOST` VALUES (4642,3122,3166,'Тестирање',NULL,17,'Радни документ тестирање контрола и радна документација тестирањаЗакони,прописи  који регулишу процес,интерни планови,процедуре,делегирање овлашћења и др.','Тестирање се спроводи путем тестова сагласности и сустинским тестовима.Тестирање се врши доказивање исправности и поузданости учињених  трансакција у одређеном временском раздобљу.','Директор Центра за интерну ревизију','Према року из плана ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:05');
INSERT INTO `FUK_AKTIVNOST` VALUES (4643,3122,3166,'Ажурирање резултата тестирања',NULL,18,'Радни документ тестирање контрола и радна документација тестирања','Резултати тестирања који су у сажетом облику у засебној радној документацији,у коначном облику се преносе у преносе у преглед контрола','Интерни ревизор','Према року из плана ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:05');
