INSERT INTO `FUK_AKTIVNOST` VALUES (4645,3122,3166,'Израда Нацрта ревизорског  извештаја',NULL,21,'Нацрт ревизорког извештаја са планом активности на примени препорука,уз нацрт.','Извештај се саставња у стандардизованој форми и садржи између осталог податке о систему који је предмет ревизије,обиму и циљевима ревизије и закључке и препоруке.','евизорски тим','Према року из плана ревизије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:05');
INSERT INTO `FUK_AKTIVNOST` VALUES (4646,3122,3166,'Достављање нацрта ревизорског извештаја',NULL,22,'Допис','Достављање Нацрта уз допис руководиоцу субјекта ревизије,којим се предлаже  термин завршног састанка.','Ревизорски тим','Одмах по изради Нацрта',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:05');
INSERT INTO `FUK_AKTIVNOST` VALUES (4647,3122,3166,'Усаглашавање  о       препорукама',NULL,23,'Записник са састанка','Одржавање завршног састанка на којем се усаглашавају препоруке са руководиоцем субјекта ревизије.На завршном састанку се: Презентује нацрт ревизорског извештаја,постизе сагласност о нивоу стручног мишљења ревизора,Постизање сагласности о препорукама и нивоу њихове важности','Ревизорски тим','Дат у допису',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:05');
INSERT INTO `FUK_AKTIVNOST` VALUES (4648,3122,3166,'Допуна плана активности',NULL,24,'Попуњен План активности на примени препорука','Одговорно лице субјекта ревизије допуњује образац „ план активности на примени препорука“ и доставња га Центру за интерну ревизију','Одговорно лице субјекта ревизије','8 дана од дана одржавања завршног састанка.',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:06');
INSERT INTO `FUK_AKTIVNOST` VALUES (4649,3122,3166,'Одговор на нацрт',NULL,25,'Одговор на нацрт','Одговорно лице субјекта ревизије  може да упути одговор на нацрт извештаја о обављеној ревизију,који садржи примедбе са доказима,сугестије и друга запажања у вези нацрта извештаја.','Одговорно лице субјекта ревизије','8 дана од дана достављања нацрта.',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:06');
INSERT INTO `FUK_AKTIVNOST` VALUES (4650,3122,3166,'Израда коначног ревизијског мишљења',NULL,26,'','У извештај се наводи одговор на нацрт,уколико је достављен  и одлучује о евентуалним несугласицама.','Ревизорски тим','Након  завршног састанка и/или одговора на нацрт',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:06');
INSERT INTO `FUK_AKTIVNOST` VALUES (4651,3122,3166,'Доношење коначног ревизорског извештаја',NULL,27,'Извештај о обављеној ревизији','Након доношења коначног ревизорског извештаја,исти се доставља и директору и одговорном лицу субјекта ревизије.','Директор центра за интерну ревизију','15.дана од дана одржавања завршног састанка',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:06');
INSERT INTO `FUK_AKTIVNOST` VALUES (4652,3122,3166,'Пријем извештаја о  извршењу плана активности',NULL,28,'Извештај о релизацији плана активности на примени препорука','Руководилац субјекта ревизије у обавези је да достави извештај о извршењу плана активности.','Руководилац субјекта ревизије','Рок из ревизорског извештаја.',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:06');
