INSERT INTO `FUK_AKTIVNOST` VALUES (4670,3124,3169,'Израда месечног плана рада',NULL,2,'Месечни план радачишћење улица','На основу решења о програму одржавања јавне хигијене израђује се месечни план рада','Директор сектора „Oдржавања хигијене Руководилац РЈ за уклањање отпада и прање јавних површина/“Зимска служба','До 25-ог у текућем месецу за наредни месец',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:08');
INSERT INTO `FUK_AKTIVNOST` VALUES (4671,3124,3169,'Израдаоперативног планарада',NULL,3,'Недељни план рада за чишћење улица','На основу месечног плана рада по програму одржавања јавне хигијене израђује се оперативни план на дневном и недељном нивоу.','Директор сектора „Oдржавања хигијене Руководилац РЈ за уклањање отпада и прање јавних површина/“Зимска службаАдминистратор РЈ','2 дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:08');
INSERT INTO `FUK_AKTIVNOST` VALUES (4672,3124,3169,'Припрема зареализацијупланова',NULL,4,'Радни налогРадна листаПутни налогПотврда','На основу израђених планова на недељном и дневном нивоу,руководилац РЈ,Координатор РЈ и пословођа израђују радне листе,радне и путне налоге и тахографски листић,након чега координатор даје усмени налог пословођама и радницима на којим реонима се ради.','Руководилац РЈКоординатор РЈПословођа РЈ','1 дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:08');
