INSERT INTO `FUK_AKTIVNOST` VALUES (4674,3124,3169,'Одлазак натерен и чишћењеулица',NULL,6,'Радне листеРадни налогПутни налог','Долазак радника у седиште предузећа и преузимање алата потребног за чишћење улица  у Граду према степену приоритета (улице у плавој зони,магистралне улице,улице са посебним третманим),на основу радне листе и усменог налога извршавају описане активности','Пословођа РЈРадник на чишћењу','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:08');
INSERT INTO `FUK_AKTIVNOST` VALUES (4675,3124,3169,'Озлазак натерен и чишћењеулица',NULL,7,'Радне листеРадни налогПутни налог','.Са свим отпадом ,који се генерише у поступку реализације активности ,поступа се у складу са Планом управљања отпадом.Пословођа на терену контролише извршење радова по реонима.','Пословођа РЈРадник на чишћењу','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:08');
INSERT INTO `FUK_AKTIVNOST` VALUES (4676,3124,3169,'По завршеткупосла повратак уседиште предузећа',NULL,8,'Радни налогПутни налог','По извршењу активности на терену оперативци попуњавају радне и путне налоге.Радни налози садрже податке о обиму и врсти извршених послова.Радници на чишћењу су у обавези да врате алат.','Пословођа РЈРадник на чишћењу','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:08');
INSERT INTO `FUK_AKTIVNOST` VALUES (4677,3124,3169,'Надзор нареализованимрадовима',NULL,9,'Грађевински дневникГрађевинска књигаМесечна фин.ситуација','Руководилац РЈ грађевинску књигу и грађевински дневник потписује у име извршиоца.Грађевинску књигу и грађевински дневник оверава надзорни орган именован од стране скупштине града-Дирекција за изградњу града.На основу потписаних ситуација од стране надзорног органа попуњава се  формулар месечна финансијска ситуација.','Руководилац РЈНадзорни органАдминистратор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:08');
