INSERT INTO `FUK_AKTIVNOST` VALUES (4703,3126,3172,'Припрема          предлога за            извршење',NULL,8,'Списак за утужење;ИОС;','Ако након послате опомене пословни партнер не измири доспеле обавезе приступа се  припреми предлога за утужење','Самостални стручни сарадник за наплату прихода','Недељно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (4704,3126,3172,'Комора           извршитеља',NULL,9,'Образац бр.2','Након тога се подноси захтев Комори извршитеља  за доделу надлежних извршитеља по сваком предмету.','Руководилац службе наплате прихода','Недељно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (4705,3126,3172,'Подношење           предлога за             извршење',NULL,10,'Предлог за извршење на основу веродостојне исправе;ИОС','Након добијања одговора од коморе извршитеља врши се допуна добијених података и  штампање предлога за извршење који се прослеђују правој служби на даљу реализацију','Самостални стручни сарадник за наплату прихода','Недељно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:11');
