INSERT INTO `FUK_AKTIVNOST` VALUES (4709,3127,3173,'Утврђивање          оквира програма                пружања             димничарских                  услуга',NULL,2,'01.август текуће године','Према смерницама Одлуке о димничарским услугама (Сл.лист града Ниша бр. 89/2005 и 38/2010) и подацима  из претходне године прикупљених од службе пореза и цена, службе матичне евиденције и ЈП Обједињена наплата утврђује се оквир програма пружања димничарских услуга','Према смерницама Одлуке о димничарским услугама (Сл.лист града Ниша бр. 89/2005 и 38/2010) и подацима  из претходне године прикупљених од службе пореза и цена, службе матичне евиденције и ЈП Обједињена наплата утврђује се оквир програма пружања димничарских услуга','Руководилац РЈ Димничар',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:12');
INSERT INTO `FUK_AKTIVNOST` VALUES (4710,3127,3173,'',NULL,3,'15.август текуће године','Дефинисање врсте димничарских услуга, времена и начина вршења истих, као и утврђивање  броја корисника на основу улазних параметара.','Дефинисање врсте димничарских услуга, времена и начина вршења истих, као и утврђивање  броја корисника на основу улазних параметара.','Руководилац РЈ Димничар',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:12');
INSERT INTO `FUK_AKTIVNOST` VALUES (4711,3127,3173,'Контрола',NULL,4,'Након израде предлога програма','Контролом се утврђује да ли су правилно дефинисане врсте услуга и време и начин вршења истих, као и утврђен број корисника','Контролом се утврђује да ли су правилно дефинисане врсте услуга и време и начин вршења истих, као и утврђен број корисника','Технички директор',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:13');
