INSERT INTO `FUK_AKTIVNOST` VALUES (4714,3127,3173,'Упућивање               Програма         Секретаријату за              комуналне                 делатности,             енергетику и               саобраћај',NULL,7,'Након одлуке НО о усвајању Програма пружања димничарских услуга','Упућивање Програма  Секретаријату за комуналне делатности, енергетику и саобраћај како би извршили проверу истог  и проследили Градском већу на сагласност','Упућивање Програма  Секретаријату за комуналне делатности, енергетику и саобраћај како би извршили проверу истог  и проследили Градском већу на сагласност','Сектор правних послова',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:13');
INSERT INTO `FUK_AKTIVNOST` VALUES (4715,3127,3173,'Решење',NULL,8,'30.август текуће године','Давање сагласности на Програм пружања димничарских услуга од стране Градског већа','Давање сагласности на Програм пружања димничарских услуга од стране Градског већа','Градско веће града Ниша',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:14');
INSERT INTO `FUK_AKTIVNOST` VALUES (4716,3127,3173,'Пријем захтева                  грађана',NULL,9,'Свакодневно од 7:30 до 15:30','Пријем захтева                  грађана','Све потребне информације о пружању димничарских услуга и пријаве проблема на димоводном каналу или објекту грађани могу добити позивом на број телефона 018/4524882','Администратор за димничарске послове',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:14');
INSERT INTO `FUK_AKTIVNOST` VALUES (4717,3127,3173,'Уписивање захтева у             Књигу позива',NULL,10,'Свакодневно од 7:30 до 15:30','Уписивање захтева у             Књигу позива','По пријему захтева, у Књигу позива, уписују се:подаци грађана који је звао (адреса и др. подаци), проблеми на димоводном каналу или објекту специјални захтеви (време посете и др.).','Администратор за димничарске послове',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:14');
INSERT INTO `FUK_AKTIVNOST` VALUES (4718,3127,3173,'Преиспитивање и          обрада захтева',NULL,11,'Свакодневно','Преиспитивање и          обрада захтева','Провера података грађана у катастру евиденције димњака, обрада захтева и приоритета.','Координатор РЈ Димничар;',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:14');
INSERT INTO `FUK_AKTIVNOST` VALUES (4719,3127,3173,'Разврсавање       захтева по реонима',NULL,13,'Након обраде захтева','Разврсавање       захтева по реонима','Територија града Ниша подељена је на 36 реона. Све захтеве, према локацији, разврставамо по реонима.','Пословођа   РЈ Димничар; Координатор РЈ Димничар',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:15');
INSERT INTO `FUK_AKTIVNOST` VALUES (4720,3127,3173,'Отварање радних                  налога',NULL,15,'Свакодневно','Отварање радних                  налога','Разврстани захтеви по реонима уписују се у радним налозима за наредни дан','Администратор за димничарске послове;Пословођа      РЈ Димничар;',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:15');
INSERT INTO `FUK_AKTIVNOST` VALUES (4721,3127,3173,'Пружање            димничарских                  услуга',NULL,16,'У току 24 сата од пријема захтева','Пружање            димничарских                  услуга','Димничар одлази на реализацију активности у реон за који је задужен. У току рада попуњава радни налог информацијама о извршеним или нереализованим радовима. У књигу димничарских услуга бележи све промене или напомене у току рада.','Пословођа  РЈ Димничар; Димничар',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:16');
INSERT INTO `FUK_AKTIVNOST` VALUES (4722,3127,3173,'Обилазак корисника',NULL,17,'Према распореду чишћење димоводних и ложишних објеката из Програма пружања димничарских услуга','Обилазак корисника','Према распореду чишћење димоводних и ложишних објеката, који је саставни део програма пружања димничарских услуга, а најмање једном годишње, реонски димничар обилази кориснике.','Димничар',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:16');
