INSERT INTO `FUK_AKTIVNOST` VALUES (4724,3127,3173,'Препоруке за             отклањање         неисправности и               санацију      димоводних канала',NULL,19,'Након извршене контроле и прегледа','Препоруке за             отклањање         неисправности и               санацију      димоводних канала','Уколико се при контроли димоводног канала утврди неисправност истог, кориснику се указује на проблем и даје упутство за санацију.','Пословођа   РЈ Димничар; Координатор РЈ Димничар',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:16');
INSERT INTO `FUK_AKTIVNOST` VALUES (4725,3127,3173,'Механичко чишћење           димиводних и      пожишних објеката и                  уређаја',NULL,21,'Након санације','Механичко чишћење           димиводних и      пожишних објеката и                  уређаја','Реонски димничар, након контроле и прегледа приступа механичком чишћењу димоводних и ложишних објеката и уређаја','Димничар;Пословођа   РЈ Димничар;',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:17');
