INSERT INTO `FUK_AKTIVNOST` VALUES (4789,3129,3176,'Извештај о утврђеном попису',NULL,13,'Одлука о вршењу редовног  пописа,Изјаве рачунополагача,Предлози за расход-Извештај пописне комисије','Израђује се извештај пописа који ће садржати вишкове и мањкове са вредносним показатељима и предлоге књижења и ликвидација утврђених вишкова и мањкова.','Пописна комисија','Према плану рада пописне комисије',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:33');
INSERT INTO `FUK_AKTIVNOST` VALUES (4790,3129,3176,'Израда годишњег извештаја о попису имовине иобавеза',NULL,14,'Одлука о вршењу редовног  пописа-Изјаве рачунополагача-Предлози за расход-Извештај о попису имовине и обавеза','Извештај о извршеном годишњем попису садржи:-стварно  и књиговодствено стање;-Утврђене разлике;-предлог централне пописне комисије о начину свођења разлике по попису на стварно стање(мањкова,вишкова,расхода,отписа потраживања и обавеза-предлог централне пописне комисије о начину свођења разлике по попису на стварно стање(мањкова,вишкова,расхо,отписа потраживања и обавеза,покретање судских процеса).-изјава лица  која рукују,односно која су задужена за имовину;-документацију која је служила за спровођење пописа.','Централана пописна комисија','25.јануар текуће за попис на дан 31.12..претходне године',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:33');
INSERT INTO `FUK_AKTIVNOST` VALUES (4791,3129,3176,'Одлука',NULL,15,'Одлука о:Начину ликвидације утврђених мањкова,Начину књижења утврђених вишкова,Отпису ненаплативих и застарелих потраживања и обавеза,Расходовању средстава,опреме и ситног инвентара,Мерама против особа одговорних за мањкове,оштећења,неусклађеност књиговодственог и стварног стања,застарелост и ненаплативост потраживања и слично','На основу Закона о рачуноводству,а на основу извештаја и приложених пописних листа,Директор предлаже Надзорном одбору,а Надзорни одбор доноси  одлуку  о:Начину ликвидације утврђених мањкова,Начину књижења утврђених вишкова,Отпису ненаплативих и застарелих потраживања и обавеза,Расходовању средстава,опреме и ситног инвентара,Мерама против особа одговорних за мањкове,оштећења,неусклађеност књиговодственог и стварног стања,застарелост и ненаплативост потраживања и слично.Директор доноси Одлуку о усвајању извештаја о попису имовине и обавеза којом одлучује о начину књижења и ликвидације утврђених мањкова и вишкова материјалних и других вредности,отпису материјалних вредности,потраживања и обавеза,расходовању основних средстава и ситног инвентара.','Директор,Надзорни одбор','27.јануар текуће за попис на дан 31.12..претходне године',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:33');
