INSERT INTO `FUK_AKTIVNOST` VALUES (4804,3131,3178,'Израда месечног               плана рада',NULL,2,'Месечни план сакупљања отпада','На основу решења о Програму сакупљања отпада и прања типских посуда (контејнера) израђује се месечни план рада','Директор сектора Управљања отпадомРуководилац РЈ Координатор РЈ','До 25-ог у текућем месецу за наредни месец',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (4805,3131,3178,'Израда              оперативног              плана рада',NULL,3,'Недељни план рада сакупљања отпада','На основу месечног плана рада по Програму сакупљања отпада и прања типских посуда (контејнера ) израђује се оперативни план на дневном и недељном нивоу.','Директор сектора Управљања отпадомРуководилац РЈ Координатор РЈ Администратор РЈ','2 дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (4806,3131,3178,'Планирање',NULL,4,'План рада запосленихДневни распоред запосленихРадна  листа за кантеПутни налог','На основу израђених планова на недељном и дневном нивоу,руководилац РЈ,Координатор РЈ и пословођа израђују радне листе,радне и путне налоге и тахографски листић,након чега координатор даје усмени налог пословођама и радницима на којим реонима се ради.','Руководилац РЈКоординатор РЈПословођа на сакупљању и транспорту отпада','1 дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:35');
INSERT INTO `FUK_AKTIVNOST` VALUES (4807,3131,3178,'Формирање                 екипа',NULL,5,'','Пословођа на сакупљању и транспорту отпада,врши формирање екипа за рад на реонима и то:У случају сакупљања отпада из типских контејнера запремине од 1100 литара, рејон-екипа се састоји из 4 члана (возач и 3 радника (2 привлаче контејнер, а 1 управља на командном тастеру за подизање контејнера). На реон може да изађе екипа са најмање 2 радника (1 радник који учествује у привлачењу контејнера, управља уређајем за подизање).У случају сакупљања отпада из подземних контејнера, рејон-екипа се састоји из 2 члана (возач  који управља на командном тастеру за подизање контејнера и 1 радник који учествује у пражњењу). У случају сакупљања отпада из сандука запремине 4м3 до 7 м3, рејон-екипа се састоји из 2 члана (возач  који управља на командном тастеру за подизање сандука и 1 радник који учествује у пражњењу).','Пословођа на сакупљању и транспорту отпада','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:35');
