INSERT INTO `FUK_AKTIVNOST` VALUES (4846,3134,3181,'Израда процедуре за идентификовање  позитивних  и негативних аспеката животне средине',NULL,3,'Процедура, записи','Стрчни сарадници у ЦЗЖСОРу свим процесима идентификују аспекте животне средине. У сарадњи са стручним сарадницима из службе квалитета постављају циљеве и програм мера за реализацију циљева.','Власници процеса','6 месеци',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:39');
INSERT INTO `FUK_AKTIVNOST` VALUES (4847,3134,3181,'Израда процедуре за  Мониторинг и мерења и праћење усаглашености са законском регулативом',NULL,4,'Процедура, записи','У поступку израде предметне процедуре сагледава се комплетна законска регулатива из области жзаштите животне средине и безбедности и здравља на раду, прате се решенја инспекцијских органа и на основу тога се констатује усаглешеност.','Власници процеса','6 месеци',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:39');
INSERT INTO `FUK_AKTIVNOST` VALUES (4848,3134,3181,'Израда процедуре за идентификацију спремности за реаговање у ванредним ситуацијама',NULL,5,'Процедуре, записи','У поступку израде предметнепроцедуре идентификују се потенцијалне ванредне ситуације и предузимају се неопходне законске мере да би се исказала спремност за реаговање у ванредним ситуацијама.','Власници процеса','6 месеци',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:39');
