INSERT INTO `FUK_AKTIVNOST` VALUES (4852,3135,3182,'Израда месечног           плана рада',NULL,2,'Месечни план сакупљања отпада','На основу решења о Програму сакупљања отпада и прања типских посуда (контејнера) израђује се месечни план рада','Директор сектора Управљања отпадомРуководилац РЈ Координатор РЈ','До 25-ог у текућем месецу за наредни месец',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:40');
INSERT INTO `FUK_AKTIVNOST` VALUES (4853,3135,3182,'Израда    оперативног плана               рада',NULL,3,'Недељни план рада сакупљања отпада','На основу месечног плана рада по Програму сакупљања отпада и прања типских посуда (контејнера ) израђује се оперативни план на дневном и недељном нивоу.','Директор сектора Управљања отпадомРуководилац РЈ Координатор РЈ Администратор РЈ','2 дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:40');
INSERT INTO `FUK_AKTIVNOST` VALUES (4854,3135,3182,'Планирање',NULL,4,'План рада запосленихДневни распоред запосленихРадна  листа за кантеПутни налог','На основу израђених планова на недељном и дневном нивоу,руководилац РЈ,Координатор РЈ и пословођа израђују радне листе,радне и путне налоге и тахографски листић,након чега координатор даје усмени налог пословођама и радницима на којим реонима се ради.','Руководилац РЈКоординатор РЈПословођа на сакупљању и транспорту отпада','1 дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:40');
INSERT INTO `FUK_AKTIVNOST` VALUES (4855,3135,3182,'Формирање екипа',NULL,5,'','Пословођа на сакупљању и транспорту отпада,врши формирање екипа за рад на реонима и то:у случају сакупљања отпада из типских канти запремине од 80 до 120 литара,рејон –екипа се састоји из 6 чланова[возач и 5 радника(2износача,2утоваривача и 1 задужен за повраћај празне посуде).На рејон може да изађе екипа са најмање 4 радника(без задуженог за повраћај празне посуде).','Пословођа на сакупљању и транспорту отпада','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:40');
