INSERT INTO `FUK_AKTIVNOST` VALUES (4863,3135,3182,'Мерење возила са         комуналним           отпадом',NULL,13,'Путни налог','Врши се мерење на колску вагу пуног возила-празна возила се не мере јер су тежине програмски унешене.У дневном извештају евидентира се број тура и количина допремљеног комуналног отпада у(м3) и у (кг) параметар.','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (4864,3135,3182,'Мерење возила са          комуналним            отпадом',NULL,14,'Путни налог','Врши се мерење на колску вагу пуног возила-празна возила се не мере јер су тежине програмски унешене.У дневном извештају евидентира се број тура и количина допремљеног комуналног отпада у(м3) и у (кг) параметар.','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (4865,3135,3182,'Истовар комуналног   отпада на депонију',NULL,15,'Путни налогИзвештај са  депонијеДневни план депоновања','Одлагање отпада на депонији врши се површинским начином одлагања, разастирањем, компактирањем и прекривањем инертним материјалом. Возило које довози чврсте одпатке, интерном саобраћајницом одређеном у Дневном плану депоновања отпада долази до одређене радне зоне (радног чела) и врши истовар комуналног отпада и инертног материјала.','ВозачКомунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (4866,3135,3182,'Излазак возила са     депоније и овера        путног налога',NULL,16,'Путни налогМернина','Уписује се време доласка/одласка ,име возача и број радника','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:41');
INSERT INTO `FUK_AKTIVNOST` VALUES (4867,3135,3182,'Одлазак на    прозивно место и         подношење    извештаја о раду',NULL,17,'Путни налогМернина','По извршењу активности на терену оперативци попуњавају радне и путне налоге.','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:41');
