INSERT INTO `FUK_AKTIVNOST` VALUES (4886,3137,3184,'Пријемконтејнерана поправку',NULL,2,'Путни налог,Контејнери и сандуци на поправци(улаз/излаз)','По налогу Директора сектора „Сакупљање и транспорт отпада“, да возач  са одређене локације подигне контејнер и одвезе на поправку у РЈ“Депонија“. Пријем контејнера врши пословођа  РЈ „Депонија“ и попуњава формулар контејнери на поправци (улаз)  у који уписује датум пријема, матични број возача, који је довезао контејнер,  име и податке са које локације је контејнер подиигнут. Документ потписују возач и пословођа','Возач возилапословођа','свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:43');
