INSERT INTO `FUK_AKTIVNOST` VALUES (4897,3138,3185,'Провера рачуна',NULL,4,'','Провера постојања потписа,жига или друге ознаке којом се може потврдити да је рачун одобрен за евиденцију.Примљени рачуни садрже референцу на број налога за набавку/уговора на темељу којег су роба/услуге/радови испоручени.На примљеним рачунима наведени су сви законски елементи рачуна: место издавања,број и датум,име,адресу и ПИБ предузећа који је испоручио робу или обавио услуге,име ( назив),адресу и ПИБ коме је испоручена роба или обављене услуге ( купца),количину и уобичајени трговачки назив испоручене робе као и врсту и количину обављених услуга,датум испоруке робе  или обављених услуга,износ цене испоручене робе или обављених услуга разврстане по пореској стопи,износ пореза разврстан по пореској стопи и збирни износ цене и пореза.Рачуни садрже детаљну спецификацију роба/услуга/радова које одговарају опису и  спрецификацији роба/услуга/радова дефинисаних налогом за набавку односно уговором.Рачунска контрола исправности износа за плаћање,контрола приложене отпремница,уговор,путне карте,хотелски документације ( рачун ,прјемнице,отпремнице и сл.) Као потврда обављене рачуноводствене контроле уписује се на рачун датум контроле и параф особе која је обавила контролу.','Организатор економског сектора,руководилац службе финансија,Референт законтролу финансијске документације -ликвидатор','Након уписа у књигу улазних рачуна',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:44');
