INSERT INTO `FUK_AKTIVNOST` VALUES (4936,3139,3188,'Уговарање',NULL,2,'Понуда о вршењу услуга,Уговор,Допис Обједињеној наплати.','Активности по овој процедури почиње потписивањем уговора о сарадњи. Координатор и пословође РЈ на терену, на стамбеним и пословним локацијама у граду достављају понуду о вршењу услуга. Један примерак  Уговора о сарадњи  чува се у архиви РЈ. Матична служба ЈКП Обједињена наплата о потписаном уговору обавештава се путем дописа  који се архивира у архиви.','Директор сектора одржавања хигијене,Руководилац РЈ, Координатор РЈПословођа РЈ.Сектор економских послова,','До 25-ог у текућем месецу за наредни месец',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:48');
INSERT INTO `FUK_AKTIVNOST` VALUES (4937,3139,3188,'Планирање',NULL,3,'Радне листе','Руководилац РЈ и координатор на основу потписаних уговора попуњавају радне листе  које су груписане по реонима и стављене у одговарајуће фасцикле. Пословође су одговорне да преузму Радне листе које садрже дневни план рада и организацију реализацију и контролу посла','Директор сектора одржавања хигијенеРуководилац РЈ за уклањање отпада и прање јавних површина/“Зимска службаАдминистратор РЈ','2 дана',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:48');
INSERT INTO `FUK_AKTIVNOST` VALUES (4938,3139,3188,'Реализација',NULL,4,'Радна листа','На основу постојећих склопљених уговора са корисницима услуга и сачињених планова рада, руководилац РЈ и координатор РЈ врше додељивање реона (територија града је подељена на четири реона) пословођама РЈ. Сваког јутра пословођа РЈ добија реон за који је задужен тог дана и распоред радника у РЈ  од руководиоца РЈ и координатора РЈ. На основу добијеног реона и распореда радника , пословођа РЈ спроводи планирање ресурса на дневном нивоу. Пословођа РЈ на основу распореда радника РЈ  попуњава радну листу , на основу којих они реализују своје задатке којим реонима се ради.','Руководилац РЈКоординатор РЈПословођа РЈ','1 дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:48');
INSERT INTO `FUK_AKTIVNOST` VALUES (4939,3139,3188,'Контрола',NULL,5,'','Kонтрола реализације активности','Руководилац РЈКоординатор РЈ','Дневно',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:48');
INSERT INTO `FUK_AKTIVNOST` VALUES (4940,3139,3188,'Преузимање (требовање) материјала исредстава за рад',NULL,6,'Евиденција материјала за реоне','Преузимање (требовање) материјала и средстава се врши из централног магацина на основу налога магацину да изда , у складу са потребама, а спроводи га координатор РЈ.  Приликом преузимања материјала и средстава рада из приручног магацина, лице задужено за магацин води евиденцију о издатим ставкама. Пословођа РЈ затим преузима средства рада и материјал, за свој реон за тај дан. По преузимању врши расподелу средстава рада и материјала екипама које су тог дана задужене за одређени реон.Евиденција преузетих средстава и материјала се води по реонима у евиденцији материјала за реоне , који се налазе у приручном  магацину. Администратор РЈ врши евиденцију преузетих хемијских средстава и материјала.','.Пословођа РЈКоординатор РЈ, Магационер, Администратор РЈХигигијеничар','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:48');
INSERT INTO `FUK_AKTIVNOST` VALUES (4941,3139,3188,'Излазак на терен',NULL,7,'Радна листа','Пословођа РЈ са радницима излази на терен и спроводи разврставање екипа у оквиру реона, на основу издатих радних листи и локација где се обавља припрема запослених ЈКП „Медиана“ Ниш за рад на терену (пресвлачење и припрема).Пословођа РЈ и радници на терену, реализују све активности према уговору за датог корисника. О свим активностима и времену реализације на појединим локацијама води се евиденција и уносе евентуалне примедбе у радним листама.','Координатор РЈ,Пословођа РЈ,Радник на чишћењу','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:48');
INSERT INTO `FUK_AKTIVNOST` VALUES (4942,3139,3188,'Извештавање',NULL,9,'Извештај о раду,Реализација чишћења,Евиденција приговора','Руководилац РЈ грађевинску књигу и грађевински дневник потписује у име извршиоца.Грађевинску књигу и грађевински дневник оверава надзорни орган именован од стране скупштине града-Дирекција за изградњу града.На основу потписаних ситуација од стране надзорног органа попуњава се  формулар месечна финансијска ситуација.','Директор сектора,Руководилац РЈ,Администратор РЈ,Координатор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:48');
