INSERT INTO `FUK_AKTIVNOST` VALUES (4986,3143,3194,'Приијем возила              од стране               возача у            контролној                служби',NULL,7,'Радни налогПутни налог','Долазак радника у седиште предузећа и преузимање  возила од контролне службе, потребног за сакупљање и транспорт отпада, на основу радне листе и усменог налога извршавају описане активности..Пословођа на терену контролише извршење радова по реонима.','Пословођа РЈВозач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:52');
INSERT INTO `FUK_AKTIVNOST` VALUES (4987,3143,3194,'Визуелна провера              техничке      исправности возила',NULL,8,'Путни налог','Визуелна провера техничке исправности возила од стране возача','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:52');
INSERT INTO `FUK_AKTIVNOST` VALUES (4988,3143,3194,'Преузимање радника      на прозивном месту',NULL,9,'Радна листа за контејнереПутни налог','Долазак на прозивно место возача са возилом','ВозачКомунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:53');
INSERT INTO `FUK_AKTIVNOST` VALUES (4989,3143,3194,'Сакупљање и             транспорт       комуналног отпада      са сеоског подручја',NULL,10,'Радна листа за контејнереПутни налог','Извршење активности на реону  на основу радне листе и усменог налога оперативци  извршавају описанеактивности.Пословођа на терену контролише извршење радова по реонима. У случају промене локације и промене броја контејнера, која се уписује у радној  листи за контејнере, измена се евидентира у евиденцији локација контејнера  и о томе се обавештава Служба гис/гпс достављањем нове евиденције контејнера','Пословођа на сакупљању и транспорту комуналног отпадаВозачКомунални радник','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:53');
