INSERT INTO `FUK_AKTIVNOST` VALUES (4991,3143,3194,'Улазак возила           на депонију',NULL,12,'Путни налогЕвиденција доласка возила','Врши се евиденција доласка возила РЈ сакупљања и транспорта отпада која довозе комунални отпад..','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:53');
INSERT INTO `FUK_AKTIVNOST` VALUES (4992,3143,3194,'Мерење возила са           комуналним              отпадом',NULL,13,'Путни налог','Врши се мерење на колску вагу пуног возила-празна возила се не мере јер су тежине програмски унешене.У дневном извештају евидентира се број тура и количина допремљеног комуналног отпада у(м3) и у (кг) параметар.','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:53');
INSERT INTO `FUK_AKTIVNOST` VALUES (4993,3143,3194,'Истовар      комуналног отпада            на депонију',NULL,14,'Путни налогИзвештај са  депонијеДневни план депоновања','Одлагање отпада на депонији врши се површинским начином одлагања, разастирањем, компактирањем и прекривањем инертним материјалом. Возило које довози чврсте одпатке, интерном саобраћајницом одређеном у Дневном плану депоновања отпада долази до одређене радне зоне (радног чела) и врши истовар комуналног отпада и инертног материјала.','ВозачКомунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:53');
INSERT INTO `FUK_AKTIVNOST` VALUES (4994,3143,3194,'Излазак возила         са депоније и         овера путног             налога',NULL,15,'Путни налогМернина','Уписује се време доласка/одласка ,име возача и број радника','Уписује се време доласка/одласка ,име возача и број радника','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:53');
INSERT INTO `FUK_AKTIVNOST` VALUES (4995,3143,3194,'Одлазак на     прозивно место и           подношење      извештаја о раду',NULL,16,'Путни налогМернина','По извршењу активности на терену оперативци попуњавају радне и путне налоге.','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:53');
INSERT INTO `FUK_AKTIVNOST` VALUES (4996,3143,3194,'Прање и            танкирање               возила',NULL,17,'Радни налогПутни налог','Након реализације посла,при повратку посаде возила у ЈКП“Медиан“\"Ниш,возач врши прање и танкирање возила.','ВозачМагационер горива и мазиваПерач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:53');
INSERT INTO `FUK_AKTIVNOST` VALUES (4997,3143,3194,'Пријава контролној      служби и провера            техничке    исправности возила',NULL,18,'Путни налогПотврда о издатој количини горива','Возач врши примопредају возила контролору техничке исправности возила Службе логистике и контроле,и истом врши пријаву о исправности возила у Радни налог-листа превентивног одржавања','ВозачКонтролор техничке исправности','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:53');
INSERT INTO `FUK_AKTIVNOST` VALUES (4998,3143,3194,'Паркирање              возила',NULL,19,'','Возач је у обавези да на прописан начин паркира возило у кругу предузећа','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:53');
INSERT INTO `FUK_AKTIVNOST` VALUES (4999,3143,3194,'Предаја путног       налога и пратеће         документације',NULL,20,'Путни налогПотврда о издатој количини гориваРадна листа','Возач предаје дежурном пословођи на сакупљању и транспорту отпада потписану радну листу са евидентираном реализацијим посла,потписани тахо листић и путни налог за возило.','ВозачПословођа на сакупљању и транспорту отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:54');
INSERT INTO `FUK_AKTIVNOST` VALUES (5000,3143,3194,'Обрада          рекламације',NULL,21,'Рекламација Регистар рекламације','Корисник услуга сакупљања и транспорта отпада може да поднесе рекламацију  на извршену услугу код обједињене наплате, кодСлужбе задужења и код РЈ „Сакупљање и транспорт отпада“. Подносилац рекламације,исту евидентира на рекламационом листу. Све поднете рекламације се прослеђују у РЈ „Сакупљање и транспорт отпада“. Координатор послова сакупљања и транспорта отпада прима рекламационе листове.Верзију рекламационог листа даје пословођи, који излази на терен истог дана, врши проверу стања и на полеђину рекламационог листа уписује чињенично стање','Руководилац РЈКординатор РЈПословођа на сакупљању и транспорту отпадаРуководилац службе евиденције корисника услуге','Попотреби',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:54');
INSERT INTO `FUK_AKTIVNOST` VALUES (5001,3143,3194,'Обрада         рекламације',NULL,22,'Рекламација Регистар рекламације','На основу чињеничног стања, координатор послова сакупљања и транспорта отпада сачињава извештај, који оверава руководилац РЈ. Оверени извештај се доставља Служби евиденције корисника услуга на даље процесуирање','Руководилац РЈКординатор РЈПословођа на сакупљању и транспорту отпадаРуководилац службе евиденције корисника услуге','Попотреби',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:54');
