INSERT INTO `FUK_AKTIVNOST` VALUES (5034,3147,3198,'Копањеканала заодвођењевода',NULL,5,'','Пословођа организују копање канала за одвођење вода .Руковаоц радном машином је одговоран за копање канала..',',Руковалац радном машином, Пословођа','Периодично',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:57');
