INSERT INTO `FUK_AKTIVNOST` VALUES (5036,3147,3198,'Анализаморфолошкогсастава поправилнику',NULL,7,'Дневни  план одлагања отпадаПлан дневног ангажовања радне снаге и машина','Водећи инжењер за одрживи развој је одговоран за тачно евидентирање количина издвојених фракција комуналног чврстог отпада.На основу измерених вредности сачињава се извештај.','Водећи инжењер за одрживи развој','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:57');
INSERT INTO `FUK_AKTIVNOST` VALUES (5037,3147,3198,'Прекривка саодговарајућиминертниматеријалом',NULL,8,'Дневни  план одлагања отпада,План дневног ангажовања радне снаге и машина,Дневни извештај','Одлагање отпада на депонији врши се површинским начином одлагања, разастирањем, компактирањем и прекривањем инертним материјалом Целокупна количина отпада која се распростре и сабије преко једне радне површине формиране у току једног радног дана по завршетку рада прекрива се инертним материјалом, који се насипа преко отпада на крају радног дана.','Руковаоци машина,Пословођа','Дневно и периодично',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:57');
