INSERT INTO `FUK_AKTIVNOST` VALUES (5039,3147,3198,'Припрема раднемашине занаредни дан',NULL,10,'Радни налог,Путни налог','По завршетку рада у 18:00h до 19:00h руковалац радном машином чисти радну машину и пријављује уочене недостатке и кварове лицу за техничку исправност-механичару. Руковалац радне машине уписује у радни налог  уочене кварове и недостаке на радним машинама консултујући се телефоном са механичарем у 18:00h. Механичар уписује кварове и недостаке наредног дана у путни налог  на основу радног налога  . У кабини радне машине мора се одржавати хигијена и она мора бити чиста. Мора постојати прва помоћ и ватрогасни апарат и на крају смене врата да су увек затворена због евентуалних временских непогода','Руковалац радном машином,Аутомеханичар,','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:57');
