INSERT INTO `FUK_AKTIVNOST` VALUES (5054,3150,3201,'Израда и достава инструкција',NULL,2,'Инструкције и стандардизовани обрасци','Одељење за јавне набавке,као носилац планирања,пре почетка поступка пријављивања потреба за предметима набавки,доставља осталим учесницима (помоћницима директора и руководиоцима)  инструкције за планирање са стандардизованим обрасцима,табелама и другом неопходном документацијом.','Шеф одељења за јавне набавке','До 15.септембра',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:59');
INSERT INTO `FUK_AKTIVNOST` VALUES (5055,3150,3201,'Утврђивање и исказивање потреба',NULL,3,'План набавки (потреба) по организационим јединицама (центри,сектори и службе)','Помоћници директора и руководиоци до 22.септембра достављају потребе за набавкама добара,услуга и радова за наредну годину за организационе јединице из свог округа.Помоћници директора и руководиоци утврђују стварне потребе за предметима набавки који су неопходни за обављање активности из свог делокруга,у складу са датим инструкцијама и критеријумима за планирање.','Помоћници директора и руководиоци','До 22.септембар',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:59');
INSERT INTO `FUK_AKTIVNOST` VALUES (5056,3150,3201,'Провера',NULL,4,'Обавештење','Шеф Одељења за јавне набавке,након пријема исказаних потреба,исте доставља менаџменту,који исте проверавају и коригују у року од 5 дана од дана пријема исказаних потреба за предметима набавки.Уочена неслагања исказаних потреба са критеријумима за планирање,други недостатаци и корекције констатују се записником.Истовремено директор именује лица која ће извршити испитивање тржишта за сваки предмет набавке.Записник потписују чланови менаџмента и остала лица која присуствују провери исказаних потреба.На основу записника шеф Одељења за јавне набавке сачињава пречишћену табелу исказаних потреба и упућује је менаџменту,руководиоцима и лицима које је директор именовао за испитивање тржишта за сваки предмет набавке','Помоћници директора и руководиоци  и шеф одељења за јавне набавке','До 20.октобра',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:59');
INSERT INTO `FUK_AKTIVNOST` VALUES (5057,3150,3201,'Извршавање исправака',NULL,5,'Обавештење','Лица именована за испитивање тржишта за сваки предмет набавке у обавези су да утврде процењену вредност набавке на основу спроведеног испитивања и истраживања тржишта предмета набавки,које укључује проверу цене,квалитета,периода гаранције,одржавања и сл.,и у обавези су да,до 20.октобра,доставе шефу Одељења за јавне набавке документацију прикупљену при испитивању и истраживању тржишта,која се доставља у писаном облику,и стандардизовану табелу која се доставља у писаном облику,потписана,и у електронском облику,путем електронске поште.','Лица именована за испитивање тржишта у  предузећу','До 27.септембар',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:59');
INSERT INTO `FUK_AKTIVNOST` VALUES (5058,3150,3201,'Обједињавање потреба',NULL,6,'Нацрт плана набавки','Одељење за јавне набавке обједињује достављене податке на основу истих одређује истоврсна добра,услуге и радове и сачињава нацрт плана набавки са свим обавезним елементима прописаним Законом,који путем електронске поште доставља менаџменту и и надлежном лицу организационе јединице у чијем су делокругу послови рачуноводства и финансија до 31.октобра.','Шеф одељења за јавне набавке','До 31.октобра',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:59');
INSERT INTO `FUK_AKTIVNOST` VALUES (5059,3150,3201,'Усклађивање с предлогом финансијског плана',NULL,7,'Предлог плана набавки','Надлежно лице организационе јединице у чијем су делокругу послови рачуноводства и финансија и шеф одељења за јавне набавке усаглашавају нацрт плана набавки са финансијским планом','Надлежно лице организационе јединице у чијем су делокругу послови рачуноводства и финансија и шеф одељења за јавне набавке','До 20.новембар',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:59');
INSERT INTO `FUK_AKTIVNOST` VALUES (5060,3150,3201,'Припрема предлога плана набавки',NULL,8,'Предлог плана набавки','Шеф одељења за јавне набавке припрема Предлог плана набавки који доставља директору,заменику директора,помоћницима директора и интерној ревизији','Шеф одељења за јавне набавке','До 28.новембар',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:41:59');
