INSERT INTO `FUK_AKTIVNOST` VALUES (5083,3151,3202,'Надзор нареализованимрадовима',NULL,16,'Грађевински дневникГрађевинска књигаМесечна фин.ситуација','Руководилац РЈ грађевинску књигу и грађевински дневник потписује у име извршиоца.Грађевинску књигу и грађевински дневник оверава надзорни орган именован од стране скупштине града-Дирекција за изградњу града.На основу потписаних ситуација од стране надзорног органа попуњава се  формулар месечна финансијска ситуација.','Руководилац РЈНадзорни органАдминистратор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:02');
