INSERT INTO `FUK_AKTIVNOST` VALUES (5127,3155,3208,'Обављање                послова',NULL,2,'Налог директора, Овлашћење директора','Помоћници директора у предузећу помажу директору у обављању његових послова утврђених Статутом. Организују и координирају рад организационих јединица у складу са планом рада. Учествују у изради и усаглашавању месечних, годишњих и средњорочних планова рада организационих јединица. Прате и анализирају реализацију планова рада и предлаже мере за унапређењепроцеса рада. Предлажу унапређивање технолошких процеса, стандардизације, система менаџмента квалитетом, унапређивање организације рада, боље коришћење капацитета. Раде на припреми и изради стручних анализа и информација. Прате прописе који се односе на делатност предузећа и о њима обавештавају  надлежне органе и службе у предузећу  са предлогом мера.Учествују у припреми уговарања и реализацији уговорних обавеза, изради бизнис планова, ценовника предузећа, јавних набавки, мишљења, информација и анализа по захтеву и за потребе органа и стручних тела предузећа и државних органа.','Директор,помоћници директора','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:05');
