INSERT INTO `FUK_AKTIVNOST` VALUES (5145,3158,3211,'Утврђивање ревизорског универзума',NULL,2,'Стратешки план ЈКП“Медиана“Ниш,Правилник о организацији и Систематизацији радних места.','Израда листе свих потенцијалних ревизија које се могу радити.','Директор Центра за интерну ревизију','Задњи квартал текуће године за планирани наступајући стратешки период',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:07');
INSERT INTO `FUK_AKTIVNOST` VALUES (5146,3158,3211,'Процена ризика и изложености и утврђивање приоритета',NULL,3,'Нацрт стратешког плана као радни документ.','Рангирање система према факторима ризика.На основу утврђене фреквенције и прегледа свих обављених ревизија,планирају се и ревизије система који су мање изложени ризику.','Директор Центра за интерну ревизију','Задњи квартал текуће године за планирани наступајући стратешки период',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:07');
INSERT INTO `FUK_AKTIVNOST` VALUES (5147,3158,3211,'Планирање потребних ресурса',NULL,4,'Нацрт стратешког плана као радни документ.','Утврђивање потребних ресурса у раду Центра за интерну ревизију за стратешки период подразумева планирање ангажовања специјалиста из одређених области , планирање повећања броја запослених у интерној ревизији и планирање потребних едукација и усавршавања.','Директор Центра за интерну ревизију','Задњи квартал текуће године за планирани наступајући стратешки период',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:08');
