INSERT INTO `FUK_AKTIVNOST` VALUES (5149,3158,3211,'Контрола',NULL,6,'','Потписом на претећи допис који је сатавни део предложеног Нацрта стратешког плана директор Центра за интерну ревизију даје сагласнст на предложени Стратешки план интерне ревизије.','Директор центра за интерну ревизију и Директор.','Одмах по изради нацрта.',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:08');
INSERT INTO `FUK_AKTIVNOST` VALUES (5150,3158,3211,'Одобрење стратешког плана',NULL,7,'Стратешки план интерне ревизије','Директор предузећа својим потписом одобрава предложени Стратешки план  интерне ревизије.','Директор','Одмах по прихватању,до краја текуће године за наступајући стратешки период.',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:08');
INSERT INTO `FUK_AKTIVNOST` VALUES (5151,3158,3211,'Утврђивање укупног броја ревизорских  дана',NULL,8,'Календар рада  и План расположивог броја ревизорских дана','Ова активност подразумева утврђивање  и  идентификовање сигурних одсустава са посла,очекиваних промена у погледу запослених и друге радне обавезе запослених за планску годину.','Директор Центра за интерну ревизију','31.12',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:08');
INSERT INTO `FUK_AKTIVNOST` VALUES (5152,3158,3211,'Анализа покривености',NULL,9,'','Разговор са Топ менџментом о обухвату осталих пружаоца услуга уверавања и консалтинга ради координације покривености ревизијом и минимизирања дуплираних послова.','Директор Центра за интерну ревизију','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:08');
INSERT INTO `FUK_AKTIVNOST` VALUES (5153,3158,3211,'Ревидирање и ажурирање стратешког плана',NULL,10,'Ревидирана и ажурирана Табела  План ревизије ревизорског универзума за  текући период.','Стратегијски план се ревидира и ажурира сваке године,где друга година текућег стратегијског плана постаје прва година новог плана и основа за годишњи план наредне године.Комплетна оцена плана треба да се врши на сваке четири до пет година,или чак и раније уколико дође до промена у организацији.','Директор Центра за интерну ревизију','31.12',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:08');
