INSERT INTO `FUK_AKTIVNOST` VALUES (5159,3158,3211,'Измена  годишњег плана интерне ревизије',NULL,17,'','Уколико се у току године дође до сазнања да су се појавили озбиљни проблеми у делу предузећа,те се покаже сврсисходност обављања ревизије тог дела,потребно је извршити измену плана интерне ревизије.','Директор Центра за интерну ревизију','У току године.',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:09');
