INSERT INTO `FUK_AKTIVNOST` VALUES (5164,3159,3212,'Ажурирање базе ГИС система',NULL,2,'План рада службе за развој ГИС/ГПС','Приликом ажурирања базе ГИС система врши се евиденција нових контејнера или корпи за отпад код корисника или искључивање старих услед промене локације.','Координатор ;Оператер ГИС-а,','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:09');
