INSERT INTO `FUK_AKTIVNOST` VALUES (5166,3159,3212,'Идентификација локација путем ГПС',NULL,4,'','У случају појаве новог контејнера или корпе за отпатке код корисника, оператер ГИС система излази на локацију ради идентификације локације путем ГПС уређаја и уноси све потребне податке о контејнеру и динамику одвоза отпада.По повратку, ГПС уређај се повезује на систем и подаци се убацују у','Оператер ГИС-а;','Дневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:09');
