INSERT INTO `FUK_AKTIVNOST` VALUES (5169,3160,3213,'Требовање робе за ресторан',NULL,2,'Образац требовања и налога за набавку','Требовање робе за ресторан врши се два пута месечно 1. и 15. У месецу и доставља се исто служби за комерцијалне послове .Приликом требовања водити рачуна о врсти и количини робе која се наручује ,како не би долазило до квара робе.','Руководилац службе општих и кадровских послова','Један дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (5170,3160,3213,'Требовање робе за кафе бифе',NULL,3,'Образац требовања и налога за набавку','Требовање робе за кафе бифе врши се до 25. у месецу за наредни месец,и доставља се исто служби за комерцијалне послове ,водећи рачуна о потребној количини и врсти робе.','Руководилац службе општих и кадровских послова','Један дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (5171,3160,3213,'Магацин робе за ресторан и кафе бифе',NULL,4,'Рачун, потврде о пријему робекалкулација цена','Са задуженом робом односити се са дужном пажњом,пратећи рокове трајања робе и складиштити је на одговарајући начин како не би дошло до квара или оштећења.','Референт,Издавалац топлог оброка,Опслужилац кафе бифеа','Један дан',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (5172,3160,3213,'Послуживање радника у ресторану',NULL,5,'','Свакодневно припремање намирница за исхрану радника',',Издавалац топлог оброка,','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (5173,3160,3213,'Послуживање радника у кафе бифе',NULL,6,'','Припремање топлих и хладних напитака и разношење према поруџбини.','Опслужилац кафе бифеа','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (5174,3160,3213,'Репрезентација',NULL,7,'Листа репрезентације,спецификација дате репрезентације','За издату робу за потребе репрезентације води се листа репрезентације.У листу  се уписује врста и количина издате робе и одмах се даје кориснику репрезнтације на оверу.Оверене листе репрезентације,достављају се наредни дан Руководиоцу службе општих и кадровских послова,а спецификација  месечне репрезентације до 2.у месецу за предходни месец..','Руководилац службе општих и кадровских послова,Опслужилац кафе бифеa Издавалац топлог оброка','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (5175,3160,3213,'Нивелација цена',NULL,8,'Попис робе-нивелација','Уколико је рок трајања робе на истеку или је истекао,обавестити Руководиоца службе општих и кадровских послова ради нивелације цена,а  уколико је рок истекао ради  расход.','Руководилац службе општих и кадровских послова,овлашћена комисија','По потреби',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:10');
INSERT INTO `FUK_AKTIVNOST` VALUES (5176,3160,3213,'Евиденција документације',NULL,9,'Отпремнице,фактуре,потврде о пријему робе,интерни преноси,записници','За примљену количину било ког артикла,обавезно је поседовање финансијског задужења.Документацију о задужењу(отпремнице,фактуре,потврде о пријему робе,интерни преноси,записници) формира се у моменту задужења и истог дана прослеђује се Сектору економских послова и Служби комерцијалних послова.У ресторанима ,наплата издате робе врши се приликом продаје,евидентирањем кроз фискалну касу и издавањем фискалног рачуна.У кафе бифеима наплата се врши свакодневно и евидентира се кроз фискалну касу.Пазар се предаје  благајни предузећа истог дана,а са локације ван седишта предузећа најкасније наредног дана за предходни дан','Опслужилац кафе бифеa Издавалац топлог оброка','По потреби',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:10');
