INSERT INTO `FUK_AKTIVNOST` VALUES (5179,3161,3214,'Сакупљањерециклабилноготпада',NULL,2,'Уговор о откупу,Месечни план реализације набавке,Радно упутство за мерење процеса','Сакупљање рециклабилног отпада из домаћинстава,од предузетника и са јавних површина . Услуга изношења и одвоза неопасног отпада на депонију - трећим лицима.','Референт за набавку и продају секундарних сировина,Пословођа за сортирање и прераду отпада,Сарадник за прераду отпада,Возач специјалног возилаКомуални радник у рециклажи','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (5180,3161,3214,'Транспортрециклабилноготпада',NULL,3,'Путни налог за возило,Радни налог за сакупљање и прераду секундарних сировинаТахо листић','У складу са динамичким планом пражњења типских посуда свакодневно пословођа за сортирање и прераду издаје путни налог за теретно моторно возило радни налог и тахо листић. Путни налог потписују одговорно лице за техничку исправност возила, овлашћено лице - пословођа и возач. Радни налог потписују возач, пословођа исарадник за прераду отпада, а тахо листић потписује возач по преузимању отпада и повратку са терена.','ПословођаВозач специјалног возила-Комуални радник у рециклажи','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:11');
INSERT INTO `FUK_AKTIVNOST` VALUES (5181,3161,3214,'Одвознекорисноготпада',NULL,4,'Радни налог за сакупљање и прераду секундарних сировина,Тахо листић,Реверс','Одвоз некорисног отпада на депонију, из процеса прераде. Са свим отпадом, који се  генерише у поступку реализације активности у оквиру предметне процедуре ,поступа се у складу са Планом управљања отпадом бр. 5818 од 28.03.2016.год.','Возач специјалног возила,Комуални радник у рециклажи','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:11');
