INSERT INTO `FUK_AKTIVNOST` VALUES (5225,3166,3219,'По завршеткупосла повратаку седиштепредузећа ивраћање алата',NULL,8,'Евиденција у месечном плану рада','По извршењу активности на терену оперативци попуњавају радне и путне налоге.Радни налози садрже податке о обиму и врсти извршених послова.Радници на прању су у обавези да врате алат.Руководилац РЈ грађевинску књигу и грађевински дневник потписује у име извршиоца.Грађевинску књигу и грађевински дневник оверава надзорни орган именован од стране скупштине града-Дирекција за изградњу града.На основу потписаних ситуација од стране надзорног органа попуњава се  формулар месечна финансијска ситуација.','Руководилац РЈНадзорни органАдминистратор РЈ','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:15');
