INSERT INTO `FUK_AKTIVNOST` VALUES (5233,3167,3220,'Пријем возила од      стране возача у    контролној служби',NULL,7,'Радни налогПутни налог','Долазак радника у седиште предузећа и преузимање  возила од контролне службе, потребног за сакупљање неопасног отпада и транспортна депонију  на основу радне листе и усменог налога извршавају описане активности..Пословођа на терену контролише извршење радова по реонима.','.Пословођа РЈВозач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:16');
