INSERT INTO `FUK_AKTIVNOST` VALUES (5236,3167,3220,'Сакупљање и           транспорт         индустријски   неопасног отпада од       правних лица',NULL,10,'Радна листа за контејнереПутни налог','Извршење активности на реону  на основу радне листе и усменог налога оперативци  извршавају описанеактивности.Пословођа на терену контролише извршење радова по реонима. У случају промене локације и промене броја контејнера, која се уписује у радној  листи за контејнере, измена се евидентира у евиденцији локација контејнера  и о томе се обавештава Служба гис/гпс достављањем нове евиденције контејнера','Пословођа на сакупљању и транспорту комуналног отпадаВозачКомунални радник','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:16');
INSERT INTO `FUK_AKTIVNOST` VALUES (5237,3167,3220,'Евентуална       комуникација са         пословођом',NULL,11,'Путни налогКомуникација путем мобилних телефона','Комуникација возача са пословођом на терену путем мобилних телефона у случају застоја у раду (квара на возилу и сл.)','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5238,3167,3220,'Улазак возила на             депонију',NULL,12,'Путни налогЕвиденција доласка возила','Врши се евиденција доласка возила РЈ сакупљања и транспорта отпада која довозе индустријски отпад..','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5239,3167,3220,'Мерење возила са        индустријским             отпадом',NULL,13,'Путни налог','Врши се мерење на колску вагу пуног возила-празна возила се не мере јер су тежине програмски унешене.У дневном извештају евидентира се број тура и количина допремљеног комуналног отпада у(м3) и у (кг) параметар.','ВозачКонтролор депоновања отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5240,3167,3220,'Истовар    индустријског отпада             депонију',NULL,14,'Путни налогИзвештај са  депонијеДневни план депоновања','Одлагање отпада на депонији врши се површинским начином одлагања, разастирањем, компактирањем и прекривањем инертним материјалом. Возило које довози чврсте одпатке, интерном саобраћајницом одређеном у Дневном плану депоновања отпада долази до одређене радне зоне (радног чела) и врши истовар комуналног отпада и инертног материјала.','ВозачКомунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5241,3167,3220,'Излазак возила са       депоније и овера        путног налога',NULL,15,'Путни налогМернина','Уписује се време доласка/одласка ,име возача и број радника','Уписује се време доласка/одласка ,име возача и број радника','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5242,3167,3220,'Одлазак на    прозивно место и        подношење     извештаја о раду',NULL,16,'Путни налогМернина','По извршењу активности на терену оперативци попуњавају радне и путне налоге.','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5243,3167,3220,'Прање и      танкирање возила',NULL,17,'Радни налогПутни налог','Након реализације посла,при повратку посаде возила у ЈКП“Медиан“\"Ниш,возач врши прање и танкирање возила.','ВозачМагационер горива и мазиваПерач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5244,3167,3220,'Пријава контролној      служби и провера    техничке исправности              возила',NULL,18,'Путни налогПотврда о издатој количини горива','Возач врши примопредају возила контролору техничке исправности возила Службе логистике и контроле,и истом врши пријаву о исправности возила у Радни налог-листа превентивног одржавања','ВозачКонтролор техничке исправности','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5245,3167,3220,'Паркирање возила',NULL,19,'','Возач је у обавези да на прописан начин паркира возило у кругу предузећа','Возач','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5246,3167,3220,'Предаја путног     налога и пратеће       документације',NULL,20,'Путни налогПотврда о издатој количини гориваРадна листа','Возач предаје дежурном пословођи на сакупљању и транспорту отпада потписану радну листу са евидентираном реализацијим посла,потписани тахо листић и путни налог за возило.','ВозачПословођа на сакупљању и транспорту отпада','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5247,3167,3220,'Обрада        рекламације',NULL,21,'Рекламација Регистар рекламације','Корисник услуга сакупљања и транспорта отпада може да поднесе рекламацију  на извршену услугу код обједињене наплате, код Службе задужења и код РЈ „Сакупљање и транспорт отпада“. Подносилац рекламације,исту евидентира на рекламационом листу. Све поднете рекламације се прослеђују у РЈ „Сакупљање и транспорт отпада“. Координатор послова сакупљања и транспорта отпада прима рекламационе листове. Верзију рекламационог листа даје пословођи, који излази на терен истог дана, врши проверу стања и на полеђину рекламационог листа уписује чињенично стање. На основу чињеничног стања, координатор послова сакупљања и транспорта отпада сачињава извештај, који оверава руководилац РЈ. Оверени извештај се доставља Служби евиденције корисника услуга на даље процесуирање. Координатор послова сакупљања и транспорта отпада у својој архиви чува верзије рекламационих листова и, на основу примљених рекламационих листова, води Регистар рекламација','Руководилац РЈКординатор РЈПословођа на сакупљању и транспорту отпадаРуководилац службе евиденције корисника услуге','По потреби',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
INSERT INTO `FUK_AKTIVNOST` VALUES (5248,3167,3220,'Извештавање',NULL,22,'Периодични извештаји','Координатор послова сакупљања и транспорта отпада припрема периодичне извештаје (дневне, месечне, годишње) које оверава руководилац Р.Ј.”Сакупљања и транспорта отпада” и исте доставља директору сектора управљања отпадом.','Директор сектораРуководилац РЈКоординатор РЈ','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:17');
