INSERT INTO `FUK_AKTIVNOST` VALUES (5251,3168,3221,'Константна исправност брава, катанаца и других система обезбеђења имовине и возила',NULL,2,'Прописана списа и евиденције  по закону о ФТО и ИСО стандардимаанализе безбедности, обавештења, наредбе извештаји,','Службеници обезбеђења  запосленима издају и од истих узимају  достављене кључеве просторија и возила. (исти се налазе на таблама у пријавницама-КПСт-ма)Обиласком објеката,канцеларија,магацина и возила на штићеној локацији контролишу закључаност истихПримају и евидентирају ковертиране-печаћене кључеве,  магацина и просторија  од  запослених рачунополагача (у  случају интервенције исти се комисиски или по налогу отварају и предају)','Службеник обезбеђења на сменизапослени који имају-користе  кључеве службених просторија-возиларуководиоци служби и запослени у комерцијали  задужени за нарезивање кључева','У току сменесве време  док је кључ у њиховом поседморају да поседују сазнања  и евиденцију ко поседује кључеве просторија',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:18');
INSERT INTO `FUK_AKTIVNOST` VALUES (5252,3168,3221,'Стална едукација у циљу подизања нивоа безбедоносне културе запослених -видно истицање знакова упозорења контрола и мониторинг)',NULL,3,'Прописана списа и евиденције  по закону о ФТО и ИСО стандардимаанализе безбедности, обавештења, наредбе извештаји,','Службеници обезбеђења у разговору са запосленима исте упозоравају на понашање,  обавештавају о појавама и пропустима који могу утицати на безбедност на штићеним локацијама  Писање обавештења, упозорења, наредбиупознавање запослених о примљеним обавештењима, наредбама, као и обавеза за спровођење истих','Службеник обезбеђења на сменируководиоц службе обез. и контролеруководиоци   оргазицационих јединица','У току сменесве време  док је кључ у њиховом поседуморају да поседују сазнања  и евиденцију ко поседује кључеве просторија',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:18');
