INSERT INTO `FUK_AKTIVNOST` VALUES (5281,3172,3225,'Израда месечног  плана рада',NULL,2,'Преглед рада зимске службе за месецРадна листа зимске службе','На основу решења о Програму рада зимске службе,који се усваја почетком новембра текуће године од стране надлежних органа Скупштине града Ниша и Штаба зимске службе','Директор сектора одржавања хигијенеРуководилац РЈ за уклањање отпада и прање јавних површина/“Зимска служба','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (5282,3172,3225,'Израдаоперативног плана  рада',NULL,3,'Дневни извештај о радуНедељни  план рада мануелног  чишћења снега','На основу месечног плана рада по Програму рада зимске службе израђује се оперативни план на дневном и недељном нивоу..','Директор сектора одржавања хигијенеРуководилац РЈ за уклањање отпада и прање јавних површина/“Зимска службаАдминистратор РЈ','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (5283,3172,3225,'Припрема за   реализацију    планова',NULL,4,'Радна листа зимске службе по реонима и проритетима','На основу израђених планова на недељном и дневном нивоу,руководилац РЈ,Координатор РЈ и пословођа израђују радне листе,радне налоге ,након чега координатор даје усмени налог пословођама и радницима на којим реонима се ради.','Руководилац РЈКоординатор РЈПословођа РЈ','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (5284,3172,3225,'Контрола',NULL,5,'Информације о активностима зимске службе','Kонтрола реализације активности','Руководилац РЈКоординатор РЈ','дневно',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (5285,3172,3225,'Одлазак радникана терен ичишћење снега',NULL,6,'Радна листа зимске службеОбразац реализације чишћења снега','У периоду од 15.11.текуће године до 31.03 наредне године,чистачи улица прелазе на режим рада зимске службе.Долазак радника у седиште предузећа и преузимање алата потребног за чишћење улица  и уклањање снега са тротоара,пешачких прелаза,тргова,мостова и других јавних површина.као и мануелном посипању соли,на основу радне листе и усменог налога извршавају описане активности. .Пословођа на терену организује извршење радова по реонима. У наведеном периоду, у зависности од временске ситуације када нема снежних падавина и поледица, зимска служба (чистачи улица и механизоване екипе) радиће све послове, на чишћењу улица, санирању дивљих депонија, а зависно од висине дневне температуре радиће на прању улица, односно на свим пословима који су предвиђени Програмом одржавања јавне хигијене','.Пословођа РЈKoмунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (5286,3172,3225,'По завршетку посла повратак у седиште предузећа',NULL,7,'Радна листа зимске службеОбразац реализације чишћења снега','По извршењу активности на терену оперативци попуњавају радне листе налоге.Радни налози садрже податке о обиму и врсти извршених послова.Радници на чишћењу су у обавези да врате алат.','Пословођа РЈKoмунални радници','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (5287,3172,3225,'Надзор нареализованимрадовима',NULL,8,'Грађевински дневникГрађевинска књигаМесечна фин.ситуација','Руководилац РЈ грађевинску књигу и грађевински дневник потписује у име извршиоца.Грађевинску књигу и грађевински дневник оверава надзорни орган именован од стране скупштине града-Дирекција за изградњу града и Штаба зимске службе.На основу потписаних ситуација од стране надзорног органа попуњава се  формулар месечна финансијска ситуација.','Руководилац РЈНадзорни органШтаб зимске службеАдминистратор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:21');
INSERT INTO `FUK_AKTIVNOST` VALUES (5288,3172,3225,'Поднети  приговори',NULL,9,'Књига рекламације','Пријем рекламације на рад службеод стране надзорног органа и Штаба зимске службе','Надзорни органШтаб зимске службеАдминистратор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:22');
INSERT INTO `FUK_AKTIVNOST` VALUES (5289,3172,3225,'Евиденцијаприговора',NULL,10,'Књига рекламације','Рекламацију на рад службе евидентира надзорни орган кроз грађевински дневник о евидентираним рекламацијама Администратор РЈ обавештава службу „контакт центар „која има задатак да се бави обрадом свих приговора','Администратор РЈНадзорни органКоординатор контакт центар','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:22');
INSERT INTO `FUK_AKTIVNOST` VALUES (5290,3172,3225,'Извештавање',NULL,11,'Обрасци реализације послова,Дневни извештај о раду,','Руководилац РЈ попуњава дневни извештај о раду који се односи на радну снагу и формулар који се односи на извршене послове и стање пратеће опреме (возила),у који уноси евидентиране рекламације од стране надзорног органа.Дневни извештаји о раду и учинку се доставља Директору сектора одржавања хигијене.','Директор сектора одржавања хигијенеРуководилац РЈ Администратор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:22');
