INSERT INTO `FUK_AKTIVNOST` VALUES (5297,3172,3226,'Механизовано  чишћење снега и  посипање соли',NULL,6,'Радна листа зимске службеДневни извештај о раду','У периоду од 15.11.текуће године до 31.03 наредне године,део сектора „Одржавање хигијене“(РЈзауклањање отпада и прање јавних површина-зимска служба)прелази на режим рада зимске службе,који обухвата машинско чишћење  и уклањање снега са коловоза и посипање сољу,а по Програму рада зимске службе.,на основу радне листе иусменог налога извршавају описане активности. .Пословођа на терену организује извршење радова по реонима. У наведеном периоду, у зависности од временске ситуације када нема снежних падавина и поледица, зимска служба (чистачи улица и механизоване екипе) радиће све послове, на чишћењу улица, санирању дивљих депонија, а зависно од висине дневне температуре радиће на прању улица, односно на свим пословима који су предвиђени Програмом одржавања јавне хигијене','.Пословођа РЈВозачЧистач улица','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:22');
INSERT INTO `FUK_AKTIVNOST` VALUES (5298,3172,3226,'Течно посипање  механизацијом   (NaCl,CaCl,MgCl)',NULL,7,'Радна листа зимске службеОбразац реализације чишћења снега','Возила опремљена за  течно посипање,користе се у посебним условима при ниским температурама,без великих падавина и при спречавању стварања поледица на саобраћајница','Пословођа РЈВозачЧистач улица','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:22');
INSERT INTO `FUK_AKTIVNOST` VALUES (5299,3172,3226,'По завршетку   посла повратак   у седиште  предузећа',NULL,8,'Радна листа зимске службеОбразац реализације чишћења снега','По извршењу активности на терену оперативци попуњавају радне листе налоге.Радни налози садрже податке о обиму и врсти извршених послова.Радници на чишћењу су у обавези да врате алат.','Пословођа РЈПерач улицаЧистач улица','По плану',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:22');
INSERT INTO `FUK_AKTIVNOST` VALUES (5300,3172,3226,'Надзор на реализованим  радовима',NULL,9,'Грађевински дневникГрађевинска књигаМесечна фин.ситуација','Руководилац РЈ грађевинску књигу и грађевински дневник потписује у име извршиоца.Грађевинску књигу и грађевински дневник оверава надзорни орган именован од стране скупштине града-Дирекција за изградњу града и Штаба зимске службе.На основу потписаних ситуација од стране надзорног органа попуњава се  формулар месечна финансијска ситуација.','Руководилац РЈНадзорни органШтаб зимске службеАдминистратор РЈ','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:22');
