INSERT INTO `FUK_AKTIVNOST` VALUES (5379,3180,3236,'Евидентирање           возила на        улазној рампи',NULL,2,'Дневни извештај РЈ Депонија Записник о одбијању депоновањаДневна евиденција за трећа лица,Захтев за уништење робе','При доласку возила на градску депонију Бубањ,врши се идентификација ,евидентирање броја извежених тура и количина изнетог смећа на депонији возилима правних и физичких лица. Приспеле количине и врсте отпада, контролор депоновања отпада евидентира у дневној евиденцији за правна и физичка лица . Уколико приликом контроле установи да се ради о отпаду чије збрињавање није дозвољено (опасан отпад, посебни токови отпада, сортирани амбалажни отпад и сл.), забрањује се пријем истог на телу депонијe и формира записник о одбијању депоновања са  подацима (o разлогу забране депоновања, као и о процењеној врсти и количини отпада).','Контролор депоновања смећаВозач','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (5380,3180,3236,'Провера              потребне          документације               и товара                 возила',NULL,3,'Захтев за уништење робе,Записник о извршеном уништењу робе.Потврда о уништењу робе','Уништење трговачке робе се врши на основу Захтева за уништење робе ,  који корисници услуге подносе ЈКП „Медиана“ Ниш (према захтеву власника или према налогу инспекције или других надлежних органа). Подносилац захтева наводи тачан разлог и предмет уништења са побројаним врстама и количинама робе.','Контролор депоновања смећаВозач','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (5381,3180,3236,'Мерење пуног                возила',NULL,4,'Дневни извештај РЈ Депонија,Мернина-пријемница','При доласку возила на градску депонију Бубањ,врши се мерење количина робе за уништење на депонији возилима правних и физичких лица.','Контролор депоновања смећаВозач','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (5382,3180,3236,'Истовар и         одговарајући          поступак са             отпадом',NULL,5,'Записник о извршеном уништењу робе.Потврда о уништењу робе  Дневни  план одлагања отпадаПлан дневног ангажовања радне снаге и машина','Правна и физичка лица остварују право да у складу са важећом Одлуком о ценама услуга на депонији, депонују на градској депонији Бубањ. Уништењу трговачке робе присуствују овлашћено лице подносиоца захтева (са овлашћењем које је дужно достави пре почетка процеса уништења), одговорно лице РЈ Депоније и запослени Службе унутрашње контроле. Након уништења напред наведена лица, попуњавају Записник о извршеном уништењу робе . Kонтролор депоновања отпада сачињава Потврду о уништењу робе У процесу уништења трговачке робе радна машина је присутна од почетка до краја. Одговорно лице за уништење је присутно и даје знак руковаоцу радне машине да изврши преласком машине преко робе више пута. Када се заврши ткз. Гажење радном машином гура уништену робу у комунални отпад и затрпава земљом са позајмишта.','ВозачРуковалац радним машинама','одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (5383,3180,3236,'Мерење празног                возила',NULL,6,'Дневни извештај РЈ Депонија,Мернина-пријемница','По завршетку депоновања отпада,врши се мерење возила трећих лица','Контролор депоновања отпада','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (5384,3180,3236,'Контрола',NULL,7,'','Реализације активности одлагања отпада','Руководилац РЈ Депонија','Свакодневно',NULL,3,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (5385,3180,3236,'Наплата           истовареног                отпада',NULL,8,'Потврда за физичко-правна лица,Потврда о извршеној услузи,Референт за фактурисање прихода','Након реализације услуге, контролор депоновања отпада сачињава Потврду о извршеној услузи. Потврда се израђује у 4 примерка, од чега се 1. примерак издаје кориснику услуге, 2. примерка се достављају Служби     пореза и цена на фактурисање, док  1. примерак остаје у РЈ „Депонија','Контролор депоновања отпада','Одмах',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (5386,3180,3236,'Евиденција        безготовинске и              готовинске                наплате',NULL,9,'Потврда за наплату,Дневна евиденција  прихода.','Након наплате услуге прослеђује се документација   финансијској служби','Контролор депоновања отпада','Свакодневно',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:30');
INSERT INTO `FUK_AKTIVNOST` VALUES (5387,3180,3236,'Извештавање',NULL,10,'Месечни извештајиГодишњи извештај РЈ Депонија','Руководилац РЈ „Депонија“ израђује месечни и годишњи извештај  о раду РЈ Депонија, који садржи: финансијски износ реализације услуге за правна и физичка лица, количине депонованог комуналног отпада од стране ЈКП „Медиана“ Ниш, количине друге врсте отпада од правних и физичких лица, као и подаци о експлоатацији возила.','Руководилац РЈ ДепонијаЦентар за ЗЖС и одрживи развој','',NULL,2,NULL,NULL,NULL,NULL,NULL,1,'2023-02-18 16:42:31');
